style:
.container {
  text-align: center;
}

.desc {
  padding: 8px; /* Padding for the content */
  border: 0.1px solid #0653cf; /* Green border */
  color: #0653cf; /* Green text */
  border-radius: 12px; /* Rounded corners */
  display: inline-block;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-bottom: 10px; /* Increased margin between each div */
  margin-right: 10px; /* Additional margin for space between divs */
  font-size: 15px; /* Increased font size */
  /*font-weight: bold;*/
}

.desc:hover {
  background-color: #0653cf;
  color: white;
}
